function qtipShow() {
  return {
    solo: true,
    type: 'fade',
    length: 400
  };
};

function qtipHide() {
  return {
    delay: "1000",
    fixed: true, 
    type: 'fade',
    length: 400
  };
};                   

function qtipRight() {
  return {
    corner: {
      target: 'rightMiddle',
      tooltip: 'leftMiddle'
    }
  };
};

function qtipLeft() {
  return {
    corner: {
      target: 'leftMiddle',
      tooltip: 'rightMiddle'
    }
  };
};


